mapper.select:
SELECT t.*, c.id as child_id, c.name as child_name, c.parent_id as child_parent_id, c.ancestors as child_ancestors, c.order_num as child_order_num, c.status as child_status, c.del_flag as child_del_flag FROM bi_object t LEFT JOIN bi_object c ON c.parent_id = t.id AND c.del_flag = '0'
AND t.name like concat('%', #{name}, '%')
AND t.status = #{status}
AND t.parent_id = #{parentId}
AND t.del_flag = '0'
order by t.parent_id, t.order_num

mapper.update:
update bi_object
name = #{name}, parent_id = #{parentId}, ancestors = #{ancestors}, order_num = #{orderNum}, status = #{status}, del_flag = #{delFlag}, longitude = #{longitude}, latitude = #{latitude}, altitude = #{altitude}, position = #{position}, area = #{area}, admin_dept = #{adminDept}, weight = #{weight}, video_feed = #{videoFeed}, props = #{props}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, count = #{count}, template_object_id = #{templateObjectId},
where id = #{id}

update bi_object

    `weight` =

        WHEN #{item.id} THEN #{item.weight}


    `update_time` =

        WHEN #{item.id} THEN #{item.updateTime}


    `update_by` =

        WHEN #{item.id} THEN #{item.updateBy}


    `count` =

        WHEN #{item.id} THEN #{item.count}


    `status` =

        WHEN #{item.id} THEN #{item.status}


where id in

    #{item.id}

update bi_object
set del_flag = '1',
    update_time = sysdate(),
    update_by = #{updateBy}
where id = #{rootObjectId}
   or FIND_IN_SET(#{rootObjectId}, ancestors)

update bi_object set ancestors =

    when #{item.id} then #{item.ancestors}

where id in

    #{item.id}

mapper.select:
WITH RECURSIVE tree AS ( -- 初始查询，获取根节点 SELECT id, parent_id, name, weight, order_num, status, create_by, create_time, update_by, update_time, remark, del_flag, props FROM bi_object WHERE id = #{rootId}
UNION ALL
-- 递归查询子节点 SELECT c.id, c.parent_id, c.name, c.weight, c.order_num, c.status, c.create_by, c.create_time, c.update_by, c.update_time, c.remark, c.del_flag, c.props FROM bi_object c INNER JOIN tree p ON c.parent_id = p.id WHERE c.del_flag = '0' ) SELECT DISTINCT t.* FROM tree t WHERE NOT EXISTS ( -- 查找没有子节点的节点（即叶子节点） SELECT 1 FROM bi_object c WHERE c.parent_id = t.id AND c.del_flag = '0' ) AND t.del_flag = '0' ORDER BY t.order_num;

SELECT EXISTS (
    SELECT 1
    FROM bi_object c1
    WHERE c1.parent_id = #{id}
      AND c1.del_flag = '0'
) AND NOT EXISTS (
    SELECT 1
    FROM bi_object c2
             JOIN bi_object c1 ON c2.parent_id = c1.id
    WHERE c1.parent_id = #{id}
      AND c1.del_flag = '0'
      AND c2.del_flag = '0'
)

SELECT t.*
FROM bi_object t


        AND t.name = #{name}


        AND t.parent_id = #{parentId}

    AND t.del_flag = '0'

order by t.parent_id, t.order_num

SELECT t.*, c.id as child_id, c.name as child_name, c.parent_id as child_parent_id, c.ancestors as child_ancestors, c.order_num as child_order_num, c.status as child_status, c.del_flag as child_del_flag FROM bi_object t LEFT JOIN bi_object c ON FIND_IN_SET(t.id, c.ancestors) AND c.del_flag = '0' WHERE t.id IN
#{id}
AND t.del_flag = '0' ORDER BY t.parent_id, t.order_num

mapper.update:
update bi_object
set count = count + #{deltaCount},
update_time = sysdate(),
update_by = #{updateBy}
where id in

    #{id}
