select.where:
b.del_flag = '0'
 and b.name like concat('%', #{name}, '%')
 and b.status = #{status}
 and b.area = #{area}
 and b.line = #{line}
 and b.admin_dept = #{adminDept}
 and b.is_leaf = #{isLeaf}

mapper.select:
SELECT
b.*,
CASE WHEN bt.project_id IS NOT NULL THEN 1 ELSE 0 END AS isSelected
FROM
bi_building b
LEFT JOIN
bi_task bt ON b.id = bt.building_id AND bt.project_id = #{projectId}

    b.del_flag = '0'
    and b.is_leaf = 1


        AND b.name LIKE CONCAT('%', #{building.name}, '%')


        AND b.status = #{building.status}


        AND b.area = #{building.area}


        AND b.line = #{building.line}


        AND b.admin_dept = #{building.adminDept}




        AND (bt.project_id IS NOT NULL) = #{building.isSelected}

mapper.update:
update bi_building
name = #{name}, is_leaf = #{isLeaf}, status = #{status}, del_flag = #{delFlag}, longitude = #{longitude}, latitude = #{latitude}, altitude = #{altitude}, address = #{address}, area = #{area}, line = #{line}, admin_dept = #{adminDept}, weight = #{weight}, video_feed = #{videoFeed}, root_object_id = #{rootObjectId}, root_property_id = #{rootPropertyId}, remark = #{remark}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}

mapper.select:
WITH RECURSIVE bridge_hierarchy AS ( /* 基本查询：直接子桥 */ SELECT b.id, b.name, b.root_object_id, b.status, b.create_time, b.is_leaf, 1 AS level FROM bi_building b JOIN bi_object o ON b.root_object_id = o.id WHERE o.parent_id = #{rootObjectId} AND b.del_flag = '0' AND o.del_flag = '0'
UNION ALL
/* 递归查询：间接子桥 */ SELECT b.id, b.name, b.root_object_id, b.status, b.create_time, b.is_leaf, h.level + 1 FROM bi_building b JOIN bi_object o ON b.root_object_id = o.id JOIN bridge_hierarchy h ON o.parent_id = h.root_object_id WHERE b.del_flag = '0' AND o.del_flag = '0' ) SELECT DISTINCT id, name, root_object_id as rootObjectId, status, create_time as createTime, is_leaf as isLeaf, level FROM bridge_hierarchy ORDER BY level
